<?phpfunction user_cp_reg() {    $str = '';    if (isset($_POST['cp_user_reg_submit'])) {        global $wpdb, $table_prefix, $campaignClass;        $cp_user_email = strip_tags(trim($_POST['cp_user_email']));        $cp_user_pass = strip_tags(trim($_POST['cp_user_pass']));        $cp_user_brief_info = strip_tags(trim($_POST['cp_user_brief_info']));        $cp_user_exp = strip_tags(trim($_POST['cp_user_exp']));                $amount_to_acheive = stripslashes($_POST['camp_asso_amt']);        $time_to_acheive = stripslashes($_POST['time_goal']);        if ($cp_user_email == '') {            $msg = '<div>Please Enter Email Address</div>';        } elseif (!$campaignClass->validEmail($cp_user_email)) {            $msg = '<div>Please Give An Valid Email Address</div>';        } elseif (username_exists($cp_user_email)) {            $msg = 'This Email Id is being used another user.try another';        } elseif ($cp_user_pass == '') {            $msg = '<div>Please Give Password</div>';        } elseif ($cp_user_brief_info == '') {            $msg = '<div>Please Give Designner Brief Info</div>';        } elseif ($cp_user_exp == '') {            $msg = '<div>Please Give Your Experience</div>';        } else {            $username = time() . $cp_user_email;            $user_id = wp_create_user($cp_user_email, $cp_user_pass, $cp_user_email);            update_user_meta($user_id, 'cp_user_brief_info', $cp_user_brief_info);            update_user_meta($user_id, 'cp_user_exp', $cp_user_exp);            update_user_meta($user_id, 'cp_user_pass', $cp_user_pass);            update_user_meta($user_id,'_amount_to_achieve',$amount_to_acheive);            update_user_meta($user_id,'_time_to_achieve',$time_to_acheive);            $msg = '<div>Your login details have been sent to your email. You can now login in with those details to create a campaign</div>';            $to = $cp_user_email;            $welcome_msg = '<p>Welcome to our site.<p>';            $welcome_msg .= '<p>Please Login to Create Campaign<p>';            $welcome_msg .= '<p>Email: ' . $cp_user_email . '</p>';            $welcome_msg .= '<p>Password: ' . $cp_user_pass . '</p>.';            send_mail_func($to, 'Welcome To Our site', $welcome_msg);        }        set_transient('cp_user_reg_msg', $msg, 30);        $location = get_permalink() . '/?cp_reg';        wp_safe_redirect($location);        exit;    }    $str .= get_transient('cp_user_reg_msg');    delete_transient('cp_user_reg_msg');    $str.= ' <div  class="sponsor_wrapper">';    $str.= '   <div class="sm_nav">';    $str.= ' <div  class="sm_nav_right">';    $str.= '  <div class="sm_reg_log">';    $str.= '<a href="' . get_permalink() . '">Home</a> <a href="' . get_permalink() . '/?cp_login">Login</a> <a href="' . get_permalink() . '/?cp_reg ">Register</a>';    $str.= '</div>';    $str.= '</div>';    $str.= '</div>';    $str.= '<div class="regi_wrapper_holder">';    $str.= ' <div class="regi_wrap">';    $str.= ' <div id="cp_user_form_msg" style="display: none;"></div>';    $str.=' <form name="user_reg" method="post" action="" enctype="multipart/form-data" onsubmit="return cp_user_reg_form();">';    $str.=' <div class="regi_field_con">';    $str.= '<label>Enter Email</label><input type="text" name="cp_user_email" id="cp_user_email" value="" class="regi_field" />';    $str.= '</div>';    $str.=' <div class="regi_field_con">';    $str.= '<label>Enter Password</label><input type="password" name="cp_user_pass" id="cp_user_pass" value="" class="regi_field" />';    $str.= '  </div>';    $str.= '        <div class="regi_field_con">';    $str.= ' <label>Enter Experience</label><input type="text" name="cp_user_exp" id="cp_user_exp" value="" class="regi_field" />';    $str.= ' </div>';    $str.=' <div class="regi_field_con">';    $str.=' <label>Designer Professional brief</label><textarea name="cp_user_brief_info" id="cp_user_brief_info" class="cp_user_brief_info" ></textarea> ';    $str.=' </div>';    $str.= '<div class="log_field_con">';    $str.= '<label>Amount You Looking From this Campaign:</label><input type="text" name="camp_asso_amt" value="" />';    $str.= '</div class="log_field_con">';    $str.= '<div class="log_field_con">';    $str.= '<label>Time To Achieve Your Goal:</label><input type="text" name="time_goal" value="" />';    $str.= '</div class="log_field_con">';    $str.= '<div class="t&c_link" style="float: left; width: 234px; text-align: left; padding: 6px 20px 8px 273px;"><input type="checkbox" name="terms" value="1" /><a href="http://sponsor-me-awards.biz/terms-conditions/">Click Here For Terms & Conditions</a></div>';    $str.='<div class="regi_field_con">';    $str.= '<label>&nbsp;</label><input type="submit" name="cp_user_reg_submit" value="Register" class="regi_btn" /> ';    $str.= '</div>';    $str.='</form>';    $str.='</div>';    $str.= '</div>';    $str.= '<div class="clr"></div>';    $str.= '</div>';    return $str;}?>